export async function initializeI18n(){try{const t=(await import("./i18n.min.js")).default;return await t.initialized,t}catch(t){return console.error("Failed to initialize i18n:",t),null}}export async function loadLanguageTranslations(t){try{let e;return e="de"===t?await import("./translations-de.min.js"):await import("./translations-en.min.js"),e.translations}catch(e){return console.error(`Failed to load translations for ${t}:`,e),null}}export function translatePage(t){t&&(document.querySelectorAll("[data-i18n]").forEach(e=>{const n=e.getAttribute("data-i18n");0===e.children.length&&(e.textContent=t.t(n))}),document.querySelectorAll("[data-i18n-placeholder]").forEach(e=>{const n=e.getAttribute("data-i18n-placeholder");e.placeholder=t.t(n)}),document.querySelectorAll("[data-i18n-title]").forEach(e=>{const n=e.getAttribute("data-i18n-title");e.title=t.t(n)}))}export function showContent(){const t=document.querySelector(".frame");t&&t.classList.add("loaded");const e=document.querySelector(".container");e&&e.classList.add("loaded")}